import React from "react";
import "./App.css";

const jsonLocalStorage = {
    setItem: (key, value) => {
        localStorage.setItem(key, JSON.stringify(value))
    },
    getItem: (key) => {
        return JSON.parse(localStorage.getItem(key))
    },
}

const fetchCat = async (text) => {
    const OPEN_API_DOMAIN = "https://cataas.com"
    const response = await fetch(`${OPEN_API_DOMAIN}/cat/says/${text}?json=true`)
    const responseJson = await response.json()
    return `${OPEN_API_DOMAIN}/${responseJson.url}`
}

const Title = (props) => {
    return <h1>{props.children}</h1>
}

const Form = ({updateMainCat}) => {
    const includesHangul = (text) => /[„Ñ±-„Öé|„Öè-„Ö£|Í∞Ä-Ìû£]/i.test(text)
    const [value, setValue] = React.useState("")
    const [errorMessage, setErrorMessage] = React.useState("")

    function handleInputChange(e) {
        const userValue = e.target.value
        setErrorMessage("")
        if (includesHangul(userValue)) {
            setErrorMessage("ÌïúÍ∏ÄÏùÄ ÏûÖÎ†•Ìï† Ïàò ÏóÜÏäµÎãàÎã§.")
        }
        setValue(userValue)
    }

    function handleFormSubmit(e) {
        e.preventDefault()
        setErrorMessage("")

        if (value === "") {
            setErrorMessage("Îπà Í∞íÏúºÎ°ú ÎßåÎì§ Ïàò ÏóÜÏäµÎãàÎã§.")
            return
        }
        updateMainCat(value)
    }

    return (
        <form onSubmit={handleFormSubmit}>
            <input
                type="text"
                name="name"
                placeholder="ÏòÅÏñ¥ ÎåÄÏÇ¨Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
                value={value}
                onChange={handleInputChange}
            />
            <button type="submit">ÏÉùÏÑ±</button>
            <p style={{color: "red"}}>{errorMessage}</p>
        </form>
    )
}

function CatItem(props) {
    return (
        <li>
            <img src={props.img} style={{width: "150px"}}/>
        </li>
    )
}

function Favorites({favorites}) {

    if (favorites.length === 0) {
        return <div>ÏÇ¨ÏßÑ ÏúÑ ÌïòÌä∏Î•º ÎàåÎü¨ Í≥†ÏñëÏù¥Î•º Ï†ÄÏû•Ìï¥Î¥êÏöî.</div>
    }

    return (
        <ul className="favorites">
            {favorites.map((cat) => (
                <CatItem img={cat} key={cat}/>
            ))}
        </ul>
    )
}

const MainCard = ({img, onHeartClick, alreadyFavorite}) => {

    const heartIcon = alreadyFavorite ? "üß°" : "ü§ç"

    return (
        <div className="main-card">
            <img src={img} alt="Í≥†ÏñëÏù¥" width="400"/>
            <button onClick={onHeartClick}>{heartIcon}</button>
        </div>
    )
}

const App = () => {
    const CAT1 =
        "https://cataas.com/cat/60b73094e04e18001194a309/says/react"

    const [counter, setCounter] = React.useState(() => {
        return jsonLocalStorage.getItem("counter")
    })

    const [mainCat, setMainCat] = React.useState(CAT1)

    const [favorites, setFavorites] = React.useState(() => {
        return jsonLocalStorage.getItem("favorites") || []
    })


    const alreadyFavorite = favorites.includes(mainCat)

    async function setintialCat() {
        const newCat = await fetchCat("First Cat");
        setMainCat(newCat)
    }

    React.useEffect(() => {
        setintialCat()
    }, [])


    const updateMainCat = async (value) => {

        const newCat = await fetchCat(value);
        setMainCat(newCat)

        setCounter((prev) => {
            const nextCounter = prev + 1
            jsonLocalStorage.setItem("counter", nextCounter)
            return nextCounter
        })

    }

    function handleHeartClick() {
        const nextFavorites = [...favorites, mainCat]
        setFavorites(nextFavorites)
        jsonLocalStorage.setItem("favorites", nextFavorites)
    }

    return (
        <div>
            <Title>{counter == null | 0 ? "" : counter + "Î≤àÏß∏ "}Í≥†ÏñëÏù¥ Í∞ÄÎùºÏÇ¨ÎåÄ</Title>
            <Form updateMainCat={updateMainCat}/>
            <MainCard img={mainCat} onHeartClick={handleHeartClick} alreadyFavorite={alreadyFavorite}/>
            <Favorites favorites={favorites}/>
        </div>
    )
}


export default App;
